mapper.select:
SELECT
c.id AS courseId,
c. name,
s.score
FROM
info_course c
LEFT JOIN info_score s ON c.id = s.course_id AND s.student_id = #{studentId}
WHERE
c.college_id = #{collegeId}
AND c.subject_id = #{subjectId}
AND c.class_id = #{classId}
AND c.grade_id = #{gradeId}
AND c.school_year = #{schoolYear}
AND c.semester = #{semester}

SELECT
c.id,
c. name
FROM
info_course c


        AND c.grade_id=#{gradeId}


        AND c.college_id=#{collegeId}


        AND c.subject_id=#{subjectId}


        AND c.class_id=#{classId}


        AND c.school_year=#{schoolYear}


        AND c.semester=#{semester}

SELECT
DISTINCT c. name
FROM
info_course c


        AND c.grade_id=#{gradeId}


        AND c.college_id=#{collegeId}


        AND c.subject_id=#{subjectId}


        AND c.class_id=#{classId}


        AND c.school_year=#{schoolYear}


        AND c.semester=#{semester}

SELECT c.id, c.college_id AS collegeId, c.subject_id AS subjectId, c.class_id AS classId, c.grade_id AS gradeId, co.name AS collegeName, su.name AS subjectName, cl.name AS className, g.name AS gradeName, c.school_year AS schoolYear, c.semester, c.name FROM info_course c LEFT JOIN info_college_subject_class co ON c.college_id=co.id LEFT JOIN info_college_subject_class su ON c.subject_id=su.id LEFT JOIN info_college_subject_class cl ON c.class_id=cl.id LEFT JOIN info_grade g ON c.grade_id=g.id
AND c.grade_id=#{gradeId}
AND c.college_id=#{collegeId}
AND c.subject_id=#{subjectId}
AND c.class_id=#{classId}
AND c.school_year=#{schoolYear}
AND c.semester=#{semester}
AND c.name LIKE '%${searchValue}%'
ORDER BY c.create_time DESC
