mapper.select:
SELECT
    c.id AS courseId,
    c. name,
    s.score
FROM
    info_course c
  LEFT JOIN info_score s ON c.id = s.course_id
WHERE
c.college_id = #{collegeId}
AND c.subject_id = #{subjectId}
AND c.class_id = #{classId}
AND c.grade_id = #{gradeId}
AND c.school_year = #{schoolYear}
AND c.semester = #{semester}

SELECT
    c.id,
    c. name
FROM
    info_course c
WHERE
c.college_id = #{collegeId}
AND c.subject_id = #{subjectId}
AND c.class_id = #{classId}
AND c.grade_id = #{gradeId}
AND c.school_year = #{schoolYear}
AND c.semester = #{semester}
