mapper.select:
SELECT s.id, s.college_id AS collegeId, s.subject_id AS subjectId, col.name AS collegeName, sub.name AS subjectName, cla.name AS className, g.name AS gradeName, s.class_id AS classId, s.grade_id AS gradeId, s.student_id AS studentId, u.username, u.nick_name AS nickName, sum(s.score) AS totalScore, s.school_year AS schoolYear, s.semester FROM info_score s LEFT JOIN sys_user u ON s.student_id = u.user_id LEFT JOIN info_college_subject_class col ON s.college_id = col.id LEFT JOIN info_college_subject_class sub ON s.subject_id = sub.id LEFT JOIN info_college_subject_class cla ON s.class_id = cla.id LEFT JOIN info_grade g ON s.grade_id = g.id
AND s.grade_id=#{gradeId}
AND s.college_id=#{collegeId}
AND s.subject_id=#{subjectId}
AND s.class_id=#{classId}
AND s.school_year=#{schoolYear}
AND s.semester=#{semester}
AND u.username like '%${searchValue}%'
AND u.nick_name like '%${searchValue}%'
AND u.state=0
GROUP BY s.student_id,s.school_year,s.semester ORDER BY totalScore DESC

SELECT c.name AS scoreName,s.score
FROM info_course c LEFT JOIN info_score s ON c.id= s.course_id

    c.grade_id=#{gradeId}
    AND c.college_id=#{collegeId}
    AND c.subject_id=#{subjectId}
    AND c.class_id=#{classId}
    AND s.student_id=#{studentId}

        AND s.school_year=#{schoolYear}


        AND c.semester=#{semester}


ORDER BY s.create_time

SELECT
    s.course_id AS courseId,
    c. name,
    s.score
FROM
    info_score s
LEFT JOIN info_course c ON s.course_id = c.id
WHERE
    s.student_id = #{studentId}
AND s.college_id = #{collegeId}
AND s.subject_id = #{subjectId}
AND s.class_id = #{classId}
AND s.grade_id = #{gradeId}
AND s.school_year = #{schoolYear}
AND s.semester = #{semester}

SELECT
s.score
FROM info_score s
LEFT JOIN info_course c ON s.course_id=c.id


        AND s.grade_id=#{gradeId}


        AND s.college_id=#{collegeId}


        AND s.subject_id=#{subjectId}


        AND s.class_id=#{classId}


        AND s.student_id=#{studentId}


        AND s.school_year=#{schoolYear}


        AND s.semester=#{semester}


ORDER BY c.name

SELECT
c.name
FROM info_course c


        AND c.grade_id=#{gradeId}


        AND c.college_id=#{collegeId}


        AND c.subject_id=#{subjectId}


        AND c.class_id=#{classId}


        AND c.school_year=#{schoolYear}


        AND c.semester=#{semester}


ORDER BY c.name

mapper.delete:
DELETE FROM info_score


        AND grade_id=#{gradeId}


        AND college_id=#{collegeId}


        AND subject_id=#{subjectId}


        AND class_id=#{classId}


        AND student_id=#{studentId}


        AND school_year=#{schoolYear}


        AND semester=#{semester}
